mapper.select:
SELECT COUNT(*) > 0
FROM notification
WHERE memberId = #{memberId}
  AND link = #{link}
  AND title = #{title}

SELECT *
FROM notification
WHERE memberId = #{memberId}
ORDER BY regDate DESC
    LIMIT #{limit}

SELECT COUNT(*) FROM notification
WHERE memberId = #{memberId}
AND isRead = FALSE

mapper.update:
UPDATE notification
SET isRead = true
WHERE memberId = #{memberId}
